import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, Global, css } from '@emotion/react';
import { AnimatePresence } from 'framer-motion';
import styled from '@emotion/styled';
import { keyframes } from '@emotion/react';
import { FaMoon, FaSun } from 'react-icons/fa';
import AOS from 'aos';
import 'aos/dist/aos.css';

// Import components
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Projects from './pages/Projects';
import About from './pages/About';
import Contact from './pages/Contact';

// Theme configuration
const lightTheme = {
  mode: 'light',
  colors: {
    primary: '#3498db',
    secondary: '#2ecc71',
    background: 'rgba(244, 248, 255, 0.9)',
    text: '#333',
    textSecondary: '#57606f',
    white: '#ffffff',
    gray: 'rgba(225, 232, 238, 0.7)',
    darkGray: '#57606f',
    backgroundGradient: {
      start: 'rgba(52, 152, 219, 0.1)',
      end: 'rgba(46, 204, 113, 0.1)'
    },
    neonPrimary: '#00ffff',  // Cyan
    neonSecondary: '#ff00ff', // Magenta
    neonAccent: '#00ff00'    // Lime Green
  },
  typography: {
    fontFamily: "'Inter', 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    h1: '2.5rem',
    h2: '2rem',
    h3: '1.5rem',
    body: '1rem',
  },
  spacing: {
    xs: '0.5rem',
    sm: '1rem',
    md: '1.5rem',
    lg: '2rem',
    xl: '3rem',
  },
  borderRadius: {
    sm: '4px',
    md: '8px',
    lg: '12px',
    round: '50%',
  },
  shadows: {
    light: '0 4px 6px rgba(0,0,0,0.1)',
    medium: '0 6px 12px rgba(0,0,0,0.15)',
    dark: '0 10px 20px rgba(0,0,0,0.2)',
  }
};

const darkTheme = {
  mode: 'dark',
  colors: {
    primary: '#4ecdc4',
    secondary: '#45b7d1',
    background: 'rgba(26, 32, 44, 0.9)',
    text: '#e2e8f0',
    textSecondary: '#a0aec0',
    white: '#1a202c',
    gray: 'rgba(45, 55, 72, 0.7)',
    darkGray: '#4a5568',
    backgroundGradient: {
      start: 'rgba(78, 205, 196, 0.1)',
      end: 'rgba(69, 183, 209, 0.1)'
    },
    neonPrimary: '#00ffff',  // Cyan
    neonSecondary: '#ff00ff', // Magenta
    neonAccent: '#00ff00'    // Lime Green
  },
  typography: lightTheme.typography,
  spacing: lightTheme.spacing,
  borderRadius: lightTheme.borderRadius,
  shadows: lightTheme.shadows,
};

const ThemeToggleButton = styled.button`
  position: fixed;
  top: ${props => props.theme.spacing.sm};
  right: ${props => props.theme.spacing.sm};
  background-color: ${props => props.theme.colors.primary};
  color: ${props => props.theme.colors.white};
  border: none;
  border-radius: ${props => props.theme.borderRadius.round};
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  z-index: 1000;
  transition: all 0.3s ease;

  &:hover {
    transform: rotate(180deg);
    background-color: ${props => props.theme.colors.secondary};
  }
`;

const globalStyles = css`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    transition: background-color 0.3s ease, color 0.3s ease;
  }
  body {
    font-family: ${props => props.theme.typography.fontFamily};
    background-color: ${props => props.theme.colors.background};
    color: ${props => props.theme.colors.text};
    line-height: 1.6;
    scroll-behavior: smooth;
    overflow-x: hidden;
  }
  a {
    text-decoration: none;
    color: ${props => props.theme.colors.primary};
    transition: color 0.3s ease;
  }
  a:hover {
    color: ${props => props.theme.colors.secondary};
  }
`;

const preventDragAndScroll = css`
  html, body {
    overscroll-behavior: none;
    overflow: hidden;
    touch-action: none;
    max-height: 100vh;
    position: fixed;
    width: 100%;
  }
`;

// Animated Gradient Background
const gradientAnimation = keyframes`
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
`;

const particleAnimation = keyframes`
  0% {
    transform: translateY(0) rotate(0deg);
    opacity: 1;
  }
  100% {
    transform: translateY(100vh) rotate(360deg);
    opacity: 0;
  }
`;

const AnimatedBackgroundContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  overflow: hidden;
  z-index: -1;
  touch-action: none;
  user-select: none;
  pointer-events: none;
  background: ${props => 
    props.theme.mode === 'light'
      ? 'linear-gradient(45deg, #ff6b6b, #4ecdc4, #45b7d1, #f9d56e)'
      : 'linear-gradient(45deg, #2c3e50, #3498db, #8e44ad, #34495e)'
  };
  background-size: 400% 400%;
  animation: ${gradientAnimation} 15s ease infinite;
`;

const Particle = styled.div`
  position: absolute;
  background: ${props => 
    props.theme.mode === 'light'
      ? `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 0.6)`
      : `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 0.3)`
  };
  border-radius: 50%;
  opacity: 0;
  pointer-events: none;
  animation: ${particleAnimation} ${props => props.duration}s linear infinite;
  left: ${props => props.left}%;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  animation-delay: ${props => props.delay}s;
`;

function AnimatedBackground({ children, theme }) {
  const containerRef = useRef(null);
  const [particles, setParticles] = useState([]);

  const generateParticles = useCallback(() => {
    const particleCount = 50;
    return Array.from({ length: particleCount }, (_, i) => ({
      id: i,
      left: Math.random() * 100,
      size: Math.random() * 20 + 5,
      duration: Math.random() * 10 + 5,
      delay: Math.random() * 5
    }));
  }, []);

  useEffect(() => {
    // Prevent default touch behaviors
    const preventDefaultTouch = (e) => {
      e.preventDefault();
    };

    const container = containerRef.current;
    if (container) {
      container.addEventListener('touchmove', preventDefaultTouch, { passive: false });
    }

    // Prevent pinch-zoom and other default touch behaviors
    document.body.addEventListener('touchstart', preventDefaultTouch, { passive: false });
    document.body.addEventListener('touchmove', preventDefaultTouch, { passive: false });

    // Generate particles
    setParticles(generateParticles());

    // Cleanup
    return () => {
      if (container) {
        container.removeEventListener('touchmove', preventDefaultTouch);
      }
      document.body.removeEventListener('touchstart', preventDefaultTouch);
      document.body.removeEventListener('touchmove', preventDefaultTouch);
    };
  }, [generateParticles, theme.mode]);

  return (
    <AnimatedBackgroundContainer ref={containerRef} theme={theme}>
      {particles.map(particle => (
        <Particle 
          key={particle.id} 
          theme={theme}
          left={particle.left}
          size={particle.size}
          duration={particle.duration}
          delay={particle.delay}
        />
      ))}
      {children}
    </AnimatedBackgroundContainer>
  );
}

function App() {
  const [theme, setTheme] = useState(() => {
    const savedTheme = localStorage.getItem('theme');
    return savedTheme === 'dark' ? darkTheme : lightTheme;
  });

  useEffect(() => {
    AOS.init({
      duration: 800,
      once: true,
    });
  }, []);

  const toggleTheme = () => {
    const newTheme = theme.mode === 'light' ? darkTheme : lightTheme;
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme.mode);
  };

  return (
    <Router>
      <ThemeProvider theme={theme}>
        <Global styles={[globalStyles, preventDragAndScroll]} />
        <ThemeToggleButton onClick={toggleTheme}>
          {theme.mode === 'light' ? <FaMoon /> : <FaSun />}
        </ThemeToggleButton>
        <AnimatedBackground theme={theme}>
          <Navbar toggleTheme={toggleTheme} theme={theme} />
          <AnimatePresence mode="wait">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/projects" element={<Projects />} />
              <Route path="/about" element={<About />} />
              <Route path="/contact" element={<Contact />} />
            </Routes>
          </AnimatePresence>
        </AnimatedBackground>
      </ThemeProvider>
    </Router>
  );
}

export default App;
