import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, Global, css } from '@emotion/react';
import styled from '@emotion/styled';
import { motion } from 'framer-motion';
import { FaMoon, FaSun } from 'react-icons/fa';
import AOS from 'aos';
import 'aos/dist/aos.css';

// Import components
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Projects from './pages/Projects';
import About from './pages/About';
import Contact from './pages/Contact';

// Theme configuration
const lightTheme = {
  mode: 'light',
  colors: {
    primary: '#3498db',
    secondary: '#2ecc71',
    background: 'rgba(244, 248, 255, 0.9)',
    text: '#333',
    textSecondary: '#57606f',
    white: '#ffffff',
    gray: 'rgba(225, 232, 238, 0.7)',
    darkGray: '#57606f',
    backgroundGradient: {
      start: 'rgba(52, 152, 219, 0.1)',
      end: 'rgba(46, 204, 113, 0.1)'
    },
    neonPrimary: '#00ffff',  // Cyan
    neonSecondary: '#ff00ff', // Magenta
    neonAccent: '#00ff00'    // Lime Green
  },
  typography: {
    fontFamily: "'Inter', 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    h1: '2.5rem',
    h2: '2rem',
    h3: '1.5rem',
    body: '1rem',
  },
  spacing: {
    xs: '0.5rem',
    sm: '1rem',
    md: '1.5rem',
    lg: '2rem',
    xl: '3rem',
  },
  borderRadius: {
    sm: '4px',
    md: '8px',
    lg: '12px',
    round: '50%',
  },
  shadows: {
    light: '0 4px 6px rgba(0,0,0,0.1)',
    medium: '0 6px 12px rgba(0,0,0,0.15)',
    dark: '0 10px 20px rgba(0,0,0,0.2)',
  }
};

const darkTheme = {
  mode: 'dark',
  colors: {
    primary: '#4ecdc4',
    secondary: '#45b7d1',
    background: 'rgba(26, 32, 44, 0.9)',
    text: '#e2e8f0',
    textSecondary: '#a0aec0',
    white: '#1a202c',
    gray: 'rgba(45, 55, 72, 0.7)',
    darkGray: '#4a5568',
    backgroundGradient: {
      start: 'rgba(78, 205, 196, 0.1)',
      end: 'rgba(69, 183, 209, 0.1)'
    },
    neonPrimary: '#00ffff',  // Cyan
    neonSecondary: '#ff00ff', // Magenta
    neonAccent: '#00ff00'    // Lime Green
  },
  typography: lightTheme.typography,
  spacing: lightTheme.spacing,
  borderRadius: lightTheme.borderRadius,
  shadows: lightTheme.shadows,
};

const ThemeToggleButton = styled.button`
  position: fixed;
  top: ${props => props.theme.spacing.sm};
  right: ${props => props.theme.spacing.sm};
  background-color: ${props => props.theme.colors.primary};
  color: ${props => props.theme.colors.white};
  border: none;
  border-radius: ${props => props.theme.borderRadius.round};
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  z-index: 1000;
  transition: all 0.3s ease;

  &:hover {
    transform: rotate(180deg);
    background-color: ${props => props.theme.colors.secondary};
  }
`;

const globalStyles = css`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    transition: background-color 0.3s ease, color 0.3s ease;
  }
  body {
    font-family: ${props => props.theme.typography.fontFamily};
    background-color: ${props => props.theme.colors.background};
    color: ${props => props.theme.colors.text};
    line-height: 1.6;
    scroll-behavior: smooth;
    overflow-x: hidden;
  }
  a {
    text-decoration: none;
    color: ${props => props.theme.colors.primary};
    transition: color 0.3s ease;
  }
  a:hover {
    color: ${props => props.theme.colors.secondary};
  }
`;

const BackgroundAnimation = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: -1;
  background: ${props => 
    props.theme.mode === 'light' 
      ? 'linear-gradient(45deg, #ffffff, #f0f0f0)' 
      : 'linear-gradient(45deg, #1a202c, #2d3748)'
  };

  &::before,
  &::after {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background-color: transparent;
    background-image: 
      radial-gradient(${props => props.theme.colors.neonPrimary} 10%, transparent 10%),
      radial-gradient(${props => props.theme.colors.neonSecondary} 10%, transparent 10%);
    background-position: 
      0 0, 
      50px 50px;
    background-size: 100px 100px;
    opacity: 0.1;
    animation: moveBackground 20s linear infinite;
    transform: rotate(0deg);
  }

  &::after {
    background-image: 
      radial-gradient(${props => props.theme.colors.neonAccent} 10%, transparent 10%),
      radial-gradient(${props => props.theme.colors.neonPrimary} 10%, transparent 10%);
    animation: moveBackground 15s linear infinite reverse;
    opacity: 0.05;
  }

  @keyframes moveBackground {
    0% {
      transform: rotate(0deg);
    }
    100% {
      transform: rotate(360deg);
    }
  }
`;

const AnimatedShape = styled(motion.div)`
  position: absolute;
  border-radius: 50%;
  background: radial-gradient(
    circle, 
    rgba(78, 205, 196, 0.2), 
    rgba(78, 205, 196, 0)
  );
`;

const AppContainer = styled.div`
  font-family: ${props => props.theme.typography.fontFamily};
  background-color: transparent;
  color: ${props => props.theme.colors.text};
  min-height: 100vh;
  position: relative;
  z-index: 1;
`;

function App() {
  const [theme, setTheme] = useState(lightTheme);

  useEffect(() => {
    AOS.init({
      duration: 800,
      once: true,
    });
  }, []);

  const toggleTheme = () => {
    setTheme(prevTheme => 
      prevTheme.mode === 'light' ? darkTheme : lightTheme
    );
  };

  const generateShapes = () => {
    const shapes = [];
    for (let i = 0; i < 5; i++) {
      shapes.push(
        <AnimatedShape
          key={i}
          initial={{ 
            x: Math.random() * window.innerWidth, 
            y: Math.random() * window.innerHeight,
            scale: 0.5,
            opacity: 0.5
          }}
          animate={{ 
            x: [
              Math.random() * window.innerWidth, 
              Math.random() * window.innerWidth, 
              Math.random() * window.innerWidth
            ],
            y: [
              Math.random() * window.innerHeight, 
              Math.random() * window.innerHeight, 
              Math.random() * window.innerHeight
            ],
            scale: [0.5, 1, 0.5],
            opacity: [0.5, 0.8, 0.5]
          }}
          transition={{
            duration: 20,
            repeat: Infinity,
            repeatType: 'reverse',
            ease: 'easeInOut'
          }}
          style={{
            width: `${100 + Math.random() * 300}px`,
            height: `${100 + Math.random() * 300}px`,
          }}
        />
      );
    }
    return shapes;
  };

  return (
    <ThemeProvider theme={theme}>
      <Global styles={globalStyles} />
      <ThemeToggleButton onClick={toggleTheme}>
        {theme.mode === 'light' ? <FaMoon /> : <FaSun />}
      </ThemeToggleButton>
      <BackgroundAnimation />
      <Router>
        <AppContainer>
          <Navbar theme={theme} />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/projects" element={<Projects />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </AppContainer>
      </Router>
    </ThemeProvider>
  );
}

export default App;
