import React from 'react';
import styled from '@emotion/styled';
import { motion } from 'framer-motion';

const AboutContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
  margin-top: 80px;  // Add space below navbar

  @media (max-width: 768px) {
    margin-top: 60px;  // Smaller margin for mobile devices
  }
`;

const ProfileImage = styled(motion.img)`
  width: 250px;
  height: 250px;
  border-radius: 50%;
  object-fit: cover;
  margin-bottom: 2rem;
  box-shadow: 0 4px 6px rgba(0,0,0,0.1);
`;

const AboutContent = styled.div`
  max-width: 700px;
  text-align: center;
`;

const About = () => {
  return (
    <AboutContainer>
      <ProfileImage
        src="/path/to/your/profile-image.jpg"  // Replace with your actual image path
        alt="Your Name"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
      />
      <AboutContent>
        <h2>About Me</h2>
        <p>
          I'm a passionate developer with expertise in web technologies. 
          I love creating efficient, scalable, and user-friendly applications. 
          My background includes working with modern frameworks and technologies, 
          always eager to learn and tackle new challenges.
        </p>
        <h3>Skills</h3>
        <p>
          JavaScript, React, Node.js, Python, HTML/CSS, Git, 
          Database Management, Cloud Technologies
        </p>
      </AboutContent>
    </AboutContainer>
  );
};

export default About;
