import React, { useRef, useEffect } from 'react';
import styled from '@emotion/styled';

const VideoBackgroundContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: -1;
`;

const StyledVideo = styled.video`
  position: absolute;
  top: 50%;
  left: 50%;
  min-width: 100%;
  min-height: 100%;
  width: auto;
  height: auto;
  transform: translateX(-50%) translateY(-50%);
  opacity: ${props => props.theme.mode === 'light' ? 0.3 : 0.7};
  filter: ${props => 
    props.theme.mode === 'light' 
      ? 'brightness(1.2) contrast(1.2)' 
      : 'brightness(0.6) contrast(1.5)'
  };
  transition: opacity 0.5s ease, filter 0.5s ease;
`;

const Overlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: ${props => 
    props.theme.mode === 'light' 
      ? 'linear-gradient(45deg, rgba(255,255,255,0.7), rgba(240,240,240,0.7))' 
      : 'linear-gradient(45deg, rgba(0,0,0,0.7), rgba(20,20,20,0.7))'
  };
  z-index: 1;
  transition: background 0.5s ease;
`;

const VideoBackground = ({ theme, children }) => {
  const videoRef = useRef(null);

  useEffect(() => {
    const video = videoRef.current;
    if (video) {
      video.playbackRate = 0.5; // Slow down video
      video.muted = true; // Mute video
      video.loop = true; // Loop video
    }
  }, []);

  return (
    <VideoBackgroundContainer theme={theme}>
      <StyledVideo 
        ref={videoRef} 
        theme={theme} 
        autoPlay 
        playsInline
      >
        <source 
          src={
            theme.mode === 'light' 
              ? '/videos/light-background.mp4' 
              : '/videos/dark-background.mp4'
          } 
          type="video/mp4" 
        />
        Your browser does not support the video tag.
      </StyledVideo>
      <Overlay theme={theme} />
      {children}
    </VideoBackgroundContainer>
  );
};

export default VideoBackground;
