import React, { useState, useEffect, useRef, useMemo } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, Global, css } from '@emotion/react';
import { AnimatePresence } from 'framer-motion';
import styled from '@emotion/styled';
import { FaMoon, FaSun } from 'react-icons/fa';
import AOS from 'aos';
import 'aos/dist/aos.css';

// Import components
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Projects from './pages/Projects';
import About from './pages/About';
import Contact from './pages/Contact';

// Theme configuration
const lightTheme = {
  mode: 'light',
  colors: {
    primary: '#3498db',
    secondary: '#2ecc71',
    background: 'rgba(244, 248, 255, 0.9)',
    text: '#333',
    textSecondary: '#57606f',
    white: '#ffffff',
    gray: 'rgba(225, 232, 238, 0.7)',
    darkGray: '#57606f',
    backgroundGradient: {
      start: 'rgba(52, 152, 219, 0.1)',
      end: 'rgba(46, 204, 113, 0.1)'
    },
    neonPrimary: '#00ffff',  // Cyan
    neonSecondary: '#ff00ff', // Magenta
    neonAccent: '#00ff00'    // Lime Green
  },
  typography: {
    fontFamily: "'Inter', 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    h1: '2.5rem',
    h2: '2rem',
    h3: '1.5rem',
    body: '1rem',
  },
  spacing: {
    xs: '0.5rem',
    sm: '1rem',
    md: '1.5rem',
    lg: '2rem',
    xl: '3rem',
  },
  borderRadius: {
    sm: '4px',
    md: '8px',
    lg: '12px',
    round: '50%',
  },
  shadows: {
    light: '0 4px 6px rgba(0,0,0,0.1)',
    medium: '0 6px 12px rgba(0,0,0,0.15)',
    dark: '0 10px 20px rgba(0,0,0,0.2)',
  }
};

const darkTheme = {
  mode: 'dark',
  colors: {
    primary: '#4ecdc4',
    secondary: '#45b7d1',
    background: 'rgba(26, 32, 44, 0.9)',
    text: '#e2e8f0',
    textSecondary: '#a0aec0',
    white: '#1a202c',
    gray: 'rgba(45, 55, 72, 0.7)',
    darkGray: '#4a5568',
    backgroundGradient: {
      start: 'rgba(78, 205, 196, 0.1)',
      end: 'rgba(69, 183, 209, 0.1)'
    },
    neonPrimary: '#00ffff',  // Cyan
    neonSecondary: '#ff00ff', // Magenta
    neonAccent: '#00ff00'    // Lime Green
  },
  typography: lightTheme.typography,
  spacing: lightTheme.spacing,
  borderRadius: lightTheme.borderRadius,
  shadows: lightTheme.shadows,
};

const ThemeToggleButton = styled.button`
  position: fixed;
  top: ${props => props.theme.spacing.sm};
  right: ${props => props.theme.spacing.sm};
  background-color: ${props => props.theme.colors.primary};
  color: ${props => props.theme.colors.white};
  border: none;
  border-radius: ${props => props.theme.borderRadius.round};
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  z-index: 1000;
  transition: all 0.3s ease;

  &:hover {
    transform: rotate(180deg);
    background-color: ${props => props.theme.colors.secondary};
  }
`;

const globalStyles = css`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    transition: background-color 0.3s ease, color 0.3s ease;
  }
  body {
    font-family: ${props => props.theme.typography.fontFamily};
    background-color: ${props => props.theme.colors.background};
    color: ${props => props.theme.colors.text};
    line-height: 1.6;
    scroll-behavior: smooth;
    overflow-x: hidden;
  }
  a {
    text-decoration: none;
    color: ${props => props.theme.colors.primary};
    transition: color 0.3s ease;
  }
  a:hover {
    color: ${props => props.theme.colors.secondary};
  }
`;

const StarryBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: -1;
  background: ${props => 
    props.theme.mode === 'light' 
      ? 'linear-gradient(to bottom, #e6e9f0 0%, #eef1f5 100%)' 
      : 'linear-gradient(to bottom, #0f2027, #203a43, #2c5364)'
  };
`;

const StarContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
`;

const Star = styled.div`
  position: absolute;
  background-color: ${props => 
    props.theme.mode === 'light' 
      ? 'rgba(100, 100, 100, 0.5)' 
      : 'rgba(255, 255, 255, 0.8)'
  };
  border-radius: 50%;
  transition: transform 0.1s ease-out, opacity 0.1s ease-out;
`;

function StarryNightBackground({ theme }) {
  const containerRef = useRef(null);
  const starsRef = useRef([]);
  const mouseRef = useRef({ x: 0, y: 0 });

  // Generate stars
  const stars = useMemo(() => {
    const starCount = 200;
    return Array.from({ length: starCount }, (_, i) => ({
      id: i,
      x: Math.random() * window.innerWidth,
      y: Math.random() * window.innerHeight,
      size: Math.random() * 3 + 1,
      speed: {
        x: (Math.random() - 0.5) * 0.5,
        y: (Math.random() - 0.5) * 0.5
      }
    }));
  }, []);

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    // Create star elements
    const starElements = stars.map(star => {
      const starEl = document.createElement('div');
      starEl.style.width = `${star.size}px`;
      starEl.style.height = `${star.size}px`;
      starEl.style.left = `${star.x}px`;
      starEl.style.top = `${star.y}px`;
      container.appendChild(starEl);
      return starEl;
    });

    starsRef.current = starElements;

    // Mouse move handler
    const handleMouseMove = (e) => {
      mouseRef.current = { x: e.clientX, y: e.clientY };
    };

    // Animation loop
    const animateStars = () => {
      const { x: mouseX, y: mouseY } = mouseRef.current;

      stars.forEach((star, index) => {
        const starEl = starsRef.current[index];
        if (!starEl) return;

        // Calculate distance from mouse
        const dx = mouseX - star.x;
        const dy = mouseY - star.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        // Parallax effect
        const maxDistance = 200;
        const parallaxFactor = Math.max(0, 1 - distance / maxDistance);

        // Move star
        star.x += star.speed.x;
        star.y += star.speed.y;

        // Wrap around screen
        if (star.x < 0) star.x = window.innerWidth;
        if (star.x > window.innerWidth) star.x = 0;
        if (star.y < 0) star.y = window.innerHeight;
        if (star.y > window.innerHeight) star.y = 0;

        // Apply transformations
        starEl.style.transform = `
          translate(${star.x}px, ${star.y}px) 
          scale(${1 + parallaxFactor * 0.5})
          opacity(${0.5 + parallaxFactor * 0.5})
        `;
      });

      requestAnimationFrame(animateStars);
    };

    // Event listeners
    window.addEventListener('mousemove', handleMouseMove);
    animateStars();

    // Cleanup
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      starElements.forEach(el => container.removeChild(el));
    };
  }, [stars]);

  return (
    <StarryBackground ref={containerRef} theme={theme}>
      <StarContainer theme={theme} />
    </StarryBackground>
  );
}

function App() {
  const [theme, setTheme] = useState(() => {
    const savedTheme = localStorage.getItem('theme');
    return savedTheme === 'dark' ? darkTheme : lightTheme;
  });

  useEffect(() => {
    AOS.init({
      duration: 800,
      once: true,
    });
  }, []);

  const toggleTheme = () => {
    const newTheme = theme.mode === 'light' ? darkTheme : lightTheme;
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme.mode);
  };

  return (
    <Router>
      <ThemeProvider theme={theme}>
        <Global styles={globalStyles} />
        <ThemeToggleButton onClick={toggleTheme}>
          {theme.mode === 'light' ? <FaMoon /> : <FaSun />}
        </ThemeToggleButton>
        <StarryNightBackground theme={theme} />
        <Navbar toggleTheme={toggleTheme} theme={theme} />
        <AnimatePresence mode="wait">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/projects" element={<Projects />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </AnimatePresence>
      </ThemeProvider>
    </Router>
  );
}

export default App;
