import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, Global, css } from '@emotion/react';
import { AnimatePresence } from 'framer-motion';
import styled from '@emotion/styled';
import { FaMoon, FaSun } from 'react-icons/fa';
import AOS from 'aos';
import 'aos/dist/aos.css';

// Import components
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Projects from './pages/Projects';
import About from './pages/About';
import Contact from './pages/Contact';

// Theme configuration
const lightTheme = {
  mode: 'light',
  colors: {
    primary: '#3498db',
    secondary: '#2ecc71',
    background: 'rgba(244, 248, 255, 0.9)',
    text: '#333',
    textSecondary: '#57606f',
    white: '#ffffff',
    gray: 'rgba(225, 232, 238, 0.7)',
    darkGray: '#57606f',
    backgroundGradient: {
      start: 'rgba(52, 152, 219, 0.1)',
      end: 'rgba(46, 204, 113, 0.1)'
    },
    neonPrimary: '#00ffff',  // Cyan
    neonSecondary: '#ff00ff', // Magenta
    neonAccent: '#00ff00'    // Lime Green
  },
  typography: {
    fontFamily: "'Inter', 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    h1: '2.5rem',
    h2: '2rem',
    h3: '1.5rem',
    body: '1rem',
  },
  spacing: {
    xs: '0.5rem',
    sm: '1rem',
    md: '1.5rem',
    lg: '2rem',
    xl: '3rem',
  },
  borderRadius: {
    sm: '4px',
    md: '8px',
    lg: '12px',
    round: '50%',
  },
  shadows: {
    light: '0 4px 6px rgba(0,0,0,0.1)',
    medium: '0 6px 12px rgba(0,0,0,0.15)',
    dark: '0 10px 20px rgba(0,0,0,0.2)',
  }
};

const darkTheme = {
  mode: 'dark',
  colors: {
    primary: '#4ecdc4',
    secondary: '#45b7d1',
    background: 'rgba(26, 32, 44, 0.9)',
    text: '#e2e8f0',
    textSecondary: '#a0aec0',
    white: '#1a202c',
    gray: 'rgba(45, 55, 72, 0.7)',
    darkGray: '#4a5568',
    backgroundGradient: {
      start: 'rgba(78, 205, 196, 0.1)',
      end: 'rgba(69, 183, 209, 0.1)'
    },
    neonPrimary: '#00ffff',  // Cyan
    neonSecondary: '#ff00ff', // Magenta
    neonAccent: '#00ff00'    // Lime Green
  },
  typography: lightTheme.typography,
  spacing: lightTheme.spacing,
  borderRadius: lightTheme.borderRadius,
  shadows: lightTheme.shadows,
};

const ThemeToggleButton = styled.button`
  position: fixed;
  top: ${props => props.theme.spacing.sm};
  right: ${props => props.theme.spacing.sm};
  background-color: ${props => props.theme.colors.primary};
  color: ${props => props.theme.colors.white};
  border: none;
  border-radius: ${props => props.theme.borderRadius.round};
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  z-index: 1000;
  transition: all 0.3s ease;

  &:hover {
    transform: rotate(180deg);
    background-color: ${props => props.theme.colors.secondary};
  }
`;

const globalStyles = css`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    transition: background-color 0.3s ease, color 0.3s ease;
  }
  body {
    font-family: ${props => props.theme.typography.fontFamily};
    background-color: ${props => props.theme.colors.background};
    color: ${props => props.theme.colors.text};
    line-height: 1.6;
    scroll-behavior: smooth;
    overflow-x: hidden;
  }
  a {
    text-decoration: none;
    color: ${props => props.theme.colors.primary};
    transition: color 0.3s ease;
  }
  a:hover {
    color: ${props => props.theme.colors.secondary};
  }
`;

const BackgroundAnimation = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: -1;
  perspective: 1000px;
  background: ${props => 
    props.theme.mode === 'light' 
      ? 'linear-gradient(45deg, #ffffff, #f0f0f0)' 
      : 'linear-gradient(45deg, #1a202c, #2d3748)'
  };
`;

const NeonLayer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: 
    radial-gradient(${props => props.theme.colors.neonPrimary} 10%, transparent 10%),
    radial-gradient(${props => props.theme.colors.neonSecondary} 10%, transparent 10%);
  background-position: 0 0, 50px 50px;
  background-size: 100px 100px;
  opacity: 0.1;
  transition: transform 0.1s ease-out;
  transform-style: preserve-3d;
`;

const NeonOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: 
    radial-gradient(${props => props.theme.colors.neonAccent} 10%, transparent 10%),
    radial-gradient(${props => props.theme.colors.neonPrimary} 10%, transparent 10%);
  background-position: 0 0, 50px 50px;
  background-size: 100px 100px;
  opacity: 0.05;
  transition: transform 0.1s ease-out;
  transform-style: preserve-3d;
`;

function InteractiveBackground({ children, theme }) {
  const backgroundRef = useRef(null);
  const layerRef1 = useRef(null);
  const layerRef2 = useRef(null);

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (!backgroundRef.current || !layerRef1.current || !layerRef2.current) return;

      const { width, height } = backgroundRef.current.getBoundingClientRect();
      const centerX = width / 2;
      const centerY = height / 2;

      // Calculate mouse position relative to center
      const mouseX = (e.clientX - centerX) / centerX;
      const mouseY = (e.clientY - centerY) / centerY;

      // Create a parallax effect with different movement speeds
      const transformLayer1 = `
        perspective(1000px) 
        rotateX(${-mouseY * 10}deg) 
        rotateY(${mouseX * 10}deg) 
        translate(${-mouseX * 20}px, ${-mouseY * 20}px)
      `;

      const transformLayer2 = `
        perspective(1000px) 
        rotateX(${-mouseY * 5}deg) 
        rotateY(${mouseX * 5}deg) 
        translate(${-mouseX * 10}px, ${-mouseY * 10}px)
      `;

      layerRef1.current.style.transform = transformLayer1;
      layerRef2.current.style.transform = transformLayer2;
    };

    const currentBackground = backgroundRef.current;
    currentBackground?.addEventListener('mousemove', handleMouseMove);

    return () => {
      currentBackground?.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <BackgroundAnimation ref={backgroundRef} theme={theme}>
      <NeonLayer ref={layerRef1} theme={theme} />
      <NeonOverlay ref={layerRef2} theme={theme} />
      {children}
    </BackgroundAnimation>
  );
}

function App() {
  const [theme, setTheme] = useState(() => {
    const savedTheme = localStorage.getItem('theme');
    return savedTheme === 'dark' ? darkTheme : lightTheme;
  });

  useEffect(() => {
    AOS.init({
      duration: 800,
      once: true,
    });
  }, []);

  const toggleTheme = () => {
    const newTheme = theme.mode === 'light' ? darkTheme : lightTheme;
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme.mode);
  };

  return (
    <Router>
      <ThemeProvider theme={theme}>
        <Global styles={globalStyles} />
        <ThemeToggleButton onClick={toggleTheme}>
          {theme.mode === 'light' ? <FaMoon /> : <FaSun />}
        </ThemeToggleButton>
        <InteractiveBackground theme={theme}>
          <Navbar toggleTheme={toggleTheme} theme={theme} />
          <AnimatePresence mode="wait">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/projects" element={<Projects />} />
              <Route path="/about" element={<About />} />
              <Route path="/contact" element={<Contact />} />
            </Routes>
          </AnimatePresence>
        </InteractiveBackground>
      </ThemeProvider>
    </Router>
  );
}

export default App;
