import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, Global, css } from '@emotion/react';
import { AnimatePresence } from 'framer-motion';
import styled from '@emotion/styled';
import { FaMoon, FaSun } from 'react-icons/fa';
import AOS from 'aos';
import 'aos/dist/aos.css';

// Import components
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Projects from './pages/Projects';
import About from './pages/About';
import Contact from './pages/Contact';

// Theme configuration
const lightTheme = {
  mode: 'light',
  colors: {
    primary: '#3498db',
    secondary: '#2ecc71',
    background: 'rgba(244, 248, 255, 0.9)',
    text: '#333',
    textSecondary: '#57606f',
    white: '#ffffff',
    gray: 'rgba(225, 232, 238, 0.7)',
    darkGray: '#57606f',
    backgroundGradient: {
      start: 'rgba(52, 152, 219, 0.1)',
      end: 'rgba(46, 204, 113, 0.1)'
    },
    neonPrimary: '#00ffff',  // Cyan
    neonSecondary: '#ff00ff', // Magenta
    neonAccent: '#00ff00'    // Lime Green
  },
  typography: {
    fontFamily: "'Inter', 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    h1: '2.5rem',
    h2: '2rem',
    h3: '1.5rem',
    body: '1rem',
  },
  spacing: {
    xs: '0.5rem',
    sm: '1rem',
    md: '1.5rem',
    lg: '2rem',
    xl: '3rem',
  },
  borderRadius: {
    sm: '4px',
    md: '8px',
    lg: '12px',
    round: '50%',
  },
  shadows: {
    light: '0 4px 6px rgba(0,0,0,0.1)',
    medium: '0 6px 12px rgba(0,0,0,0.15)',
    dark: '0 10px 20px rgba(0,0,0,0.2)',
  }
};

const darkTheme = {
  mode: 'dark',
  colors: {
    primary: '#4ecdc4',
    secondary: '#45b7d1',
    background: 'rgba(26, 32, 44, 0.9)',
    text: '#e2e8f0',
    textSecondary: '#a0aec0',
    white: '#1a202c',
    gray: 'rgba(45, 55, 72, 0.7)',
    darkGray: '#4a5568',
    backgroundGradient: {
      start: 'rgba(78, 205, 196, 0.1)',
      end: 'rgba(69, 183, 209, 0.1)'
    },
    neonPrimary: '#00ffff',  // Cyan
    neonSecondary: '#ff00ff', // Magenta
    neonAccent: '#00ff00'    // Lime Green
  },
  typography: lightTheme.typography,
  spacing: lightTheme.spacing,
  borderRadius: lightTheme.borderRadius,
  shadows: lightTheme.shadows,
};

const globalStyles = theme => css`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    transition: background-color 0.3s ease, color 0.3s ease;
  }
  body {
    font-family: ${theme.typography.fontFamily};
    background-color: ${theme.colors.background};
    color: ${theme.colors.text};
    line-height: 1.6;
    scroll-behavior: smooth;
    overflow-x: hidden;
  }
  a {
    text-decoration: none;
    color: ${theme.colors.primary};
    transition: color 0.3s ease;
  }
  a:hover {
    color: ${theme.colors.secondary};
  }
`;

const InteractiveBackgroundContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: -1;
  perspective: 1000px;
  background: ${props => 
    props.theme.mode === 'light' 
      ? 'linear-gradient(45deg, #e6f2ff, #f0f8ff)' 
      : 'linear-gradient(45deg, #0f2027, #203a43)'
  };
`;

const StarLayer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
`;

const Star = styled.div`
  position: absolute;
  background-color: ${props => 
    props.theme.mode === 'light' 
      ? 'rgba(100, 100, 200, 0.6)' 
      : 'rgba(255, 255, 255, 0.8)'
  };
  border-radius: 50%;
  opacity: ${props => props.opacity};
  transition: all 0.3s ease-out;
  box-shadow: 0 0 10px ${props => 
    props.theme.mode === 'light' 
      ? 'rgba(100, 100, 200, 0.3)' 
      : 'rgba(255, 255, 255, 0.5)'
  };
`;

const NeonOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: 
    radial-gradient(${props => props.theme.colors.neonPrimary} 2%, transparent 2%),
    radial-gradient(${props => props.theme.colors.neonSecondary} 2%, transparent 2%);
  background-position: 0 0, 50px 50px;
  background-size: 100px 100px;
  opacity: 0.1;
  transition: transform 0.3s ease-out;
  transform-style: preserve-3d;
`;

function InteractiveBackground({ children, theme }) {
  const containerRef = useRef(null);
  const starsRef = useRef([]);
  const mouseRef = useRef({ x: 0, y: 0 });
  const animationRef = useRef(null);

  // Generate stars with more complex properties
  const stars = useMemo(() => {
    const starCount = 300;
    return Array.from({ length: starCount }, (_, i) => ({
      id: i,
      x: Math.random() * window.innerWidth,
      y: Math.random() * window.innerHeight,
      size: Math.random() * 3 + 0.5,
      opacity: Math.random() * 0.7 + 0.3,
      speed: {
        x: (Math.random() - 0.5) * 0.3,
        y: (Math.random() - 0.5) * 0.3
      },
      twinkleSpeed: Math.random() * 0.02 + 0.01,
      baseOpacity: Math.random() * 0.7 + 0.3
    }));
  }, []);

  const createStarElements = useCallback(() => {
    const container = containerRef.current;
    if (!container) return [];

    return stars.map(star => {
      const starEl = document.createElement('div');
      starEl.style.width = `${star.size}px`;
      starEl.style.height = `${star.size}px`;
      starEl.style.left = `${star.x}px`;
      starEl.style.top = `${star.y}px`;
      starEl.style.opacity = `${star.opacity}`;
      container.appendChild(starEl);
      return starEl;
    });
  }, [stars]);

  const animateStars = useCallback(() => {
    const { x: mouseX, y: mouseY } = mouseRef.current;

    stars.forEach((star, index) => {
      const starEl = starsRef.current[index];
      if (!starEl) return;

      // Advanced movement with mouse interaction
      const dx = mouseX - star.x;
      const dy = mouseY - star.y;
      const distance = Math.sqrt(dx * dx + dy * dy);

      // Twinkle effect
      star.opacity = star.baseOpacity + 
        Math.sin(Date.now() * star.twinkleSpeed) * 0.2;

      // Parallax and mouse repulsion
      const maxDistance = 300;
      const parallaxFactor = Math.max(0, 1 - distance / maxDistance);
      const repulsionFactor = distance < 100 ? (1 - distance / 100) : 0;

      // Move star
      star.x += star.speed.x * (1 + parallaxFactor);
      star.y += star.speed.y * (1 + parallaxFactor);

      // Wrap around screen with slight randomness
      if (star.x < 0) star.x = window.innerWidth + Math.random() * 50;
      if (star.x > window.innerWidth) star.x = -Math.random() * 50;
      if (star.y < 0) star.y = window.innerHeight + Math.random() * 50;
      if (star.y > window.innerHeight) star.y = -Math.random() * 50;

      // Apply transformations
      starEl.style.transform = `
        translate(${star.x}px, ${star.y}px) 
        scale(${1 + parallaxFactor * 0.5 + repulsionFactor})
      `;
      starEl.style.opacity = `${star.opacity * (1 - repulsionFactor * 0.5)}`;
    });

    animationRef.current = requestAnimationFrame(animateStars);
  }, [stars]);

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    // Create star elements
    starsRef.current = createStarElements();

    // Mouse move handler
    const handleMouseMove = (e) => {
      mouseRef.current = { x: e.clientX, y: e.clientY };
    };

    // Start animation
    animationRef.current = requestAnimationFrame(animateStars);

    // Event listeners
    window.addEventListener('mousemove', handleMouseMove);

    // Cleanup
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);

      // Cancel animation and remove star elements
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
      starsRef.current.forEach(el => container.removeChild(el));
    };
  }, [createStarElements, animateStars]);

  return (
    <InteractiveBackgroundContainer ref={containerRef} theme={theme}>
      <StarLayer theme={theme} />
      <NeonOverlay theme={theme} />
      {children}
    </InteractiveBackgroundContainer>
  );
}

function App() {
  const [theme, setTheme] = useState(() => {
    const savedTheme = localStorage.getItem('theme');
    return savedTheme === 'dark' ? darkTheme : lightTheme;
  });

  const toggleTheme = () => {
    const newTheme = theme.mode === 'light' ? darkTheme : lightTheme;
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme.mode);
  };

  return (
    <Router>
      <ThemeProvider theme={theme}>
        <Global styles={globalStyles(theme)} />
        <InteractiveBackground theme={theme}>
          <Navbar toggleTheme={toggleTheme} theme={theme} />
          <AnimatePresence mode="wait">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/projects" element={<Projects />} />
              <Route path="/about" element={<About />} />
              <Route path="/contact" element={<Contact />} />
            </Routes>
          </AnimatePresence>
        </InteractiveBackground>
      </ThemeProvider>
    </Router>
  );
}

export default App;
