import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, Global, css } from '@emotion/react';
import styled from '@emotion/styled';
import { motion } from 'framer-motion';
import { FaMoon, FaSun } from 'react-icons/fa';
import AOS from 'aos';
import 'aos/dist/aos.css';
import { keyframes } from '@emotion/react';

// Import components
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Projects from './pages/Projects';
import About from './pages/About';
import Contact from './pages/Contact';

// Theme configuration
const lightTheme = {
  mode: 'light',
  colors: {
    primary: '#3498db',
    secondary: '#2ecc71',
    background: 'rgba(244, 248, 255, 0.9)',
    text: '#333',
    textSecondary: '#57606f',
    white: '#ffffff',
    gray: 'rgba(225, 232, 238, 0.7)',
    darkGray: '#57606f',
    backgroundColors: [
      '#ff6b6b',   // Pastel Red
      '#4ecdc4',   // Turquoise
      '#45b7d1',   // Sky Blue
      '#f7d794',   // Soft Yellow
      '#ff9ff3',   // Soft Pink
      '#a29bfe'    // Lavender
    ]
  },
  typography: {
    fontFamily: "'Inter', 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    h1: '2.5rem',
    h2: '2rem',
    h3: '1.5rem',
    body: '1rem',
  },
  spacing: {
    xs: '0.5rem',
    sm: '1rem',
    md: '1.5rem',
    lg: '2rem',
    xl: '3rem',
  },
  borderRadius: {
    sm: '4px',
    md: '8px',
    lg: '12px',
    round: '50%',
  },
  shadows: {
    light: '0 4px 6px rgba(0,0,0,0.1)',
    medium: '0 6px 12px rgba(0,0,0,0.15)',
    dark: '0 10px 20px rgba(0,0,0,0.2)',
  }
};

const darkTheme = {
  mode: 'dark',
  colors: {
    primary: '#4ecdc4',
    secondary: '#45b7d1',
    background: 'rgba(26, 32, 44, 0.9)',
    text: '#e2e8f0',
    textSecondary: '#a0aec0',
    white: '#1a202c',
    gray: 'rgba(45, 55, 72, 0.7)',
    darkGray: '#4a5568',
    backgroundColors: [
      '#ff6b6b',   // Pastel Red
      '#4ecdc4',   // Turquoise
      '#45b7d1',   // Sky Blue
      '#f7d794',   // Soft Yellow
      '#ff9ff3',   // Soft Pink
      '#a29bfe'    // Lavender
    ]
  },
  typography: lightTheme.typography,
  spacing: lightTheme.spacing,
  borderRadius: lightTheme.borderRadius,
  shadows: lightTheme.shadows,
};

const backgroundAnimation = keyframes`
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
`;

const gradientAnimation = keyframes`
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
`;

const BackgroundAnimation = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: -1;

  // Base background with gradient animation
  background: ${props => 
    props.theme.mode === 'light' 
      ? `linear-gradient(-45deg, ${props.theme.colors.backgroundColors.join(', ')})`
      : `linear-gradient(-45deg, ${props.theme.colors.backgroundColors.map(color => color + '99').join(', ')})`
  };
  background-size: 400% 400%;
  animation: ${backgroundAnimation} 15s ease infinite;

  // Overlay with moving gradient shapes
  &::before,
  &::after {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: ${props => 
      props.theme.mode === 'light' 
        ? `radial-gradient(circle at center, 
            ${props.theme.colors.backgroundColors[0]} 0%, 
            ${props.theme.colors.backgroundColors[1]} 25%, 
            ${props.theme.colors.backgroundColors[2]} 50%, 
            ${props.theme.colors.backgroundColors[3]} 75%, 
            ${props.theme.colors.backgroundColors[4]} 100%)`
        : `radial-gradient(circle at center, 
            ${props.theme.colors.backgroundColors[0]}99 0%, 
            ${props.theme.colors.backgroundColors[1]}99 25%, 
            ${props.theme.colors.backgroundColors[2]}99 50%, 
            ${props.theme.colors.backgroundColors[3]}99 75%, 
            ${props.theme.colors.backgroundColors[4]}99 100%)`
    };
    opacity: 0.1;
    animation: ${gradientAnimation} 20s linear infinite;
    transform-origin: center;
  }

  &::after {
    background: ${props => 
      props.theme.mode === 'light' 
        ? `radial-gradient(circle at center, 
            ${props.theme.colors.backgroundColors[5]} 0%, 
            ${props.theme.colors.backgroundColors[4]} 25%, 
            ${props.theme.colors.backgroundColors[3]} 50%, 
            ${props.theme.colors.backgroundColors[2]} 75%, 
            ${props.theme.colors.backgroundColors[1]} 100%)`
        : `radial-gradient(circle at center, 
            ${props.theme.colors.backgroundColors[5]}99 0%, 
            ${props.theme.colors.backgroundColors[4]}99 25%, 
            ${props.theme.colors.backgroundColors[3]}99 50%, 
            ${props.theme.colors.backgroundColors[2]}99 75%, 
            ${props.theme.colors.backgroundColors[1]}99 100%)`
    };
    opacity: 0.05;
    animation: ${gradientAnimation} 15s linear infinite reverse;
  }
`;

const ThemeToggleButton = styled.button`
  position: fixed;
  top: ${props => props.theme.spacing.sm};
  right: ${props => props.theme.spacing.sm};
  background-color: ${props => props.theme.colors.primary};
  color: ${props => props.theme.colors.white};
  border: none;
  border-radius: ${props => props.theme.borderRadius.round};
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  z-index: 1000;
  transition: all 0.3s ease;

  &:hover {
    transform: rotate(180deg);
    background-color: ${props => props.theme.colors.secondary};
  }
`;

const globalStyles = css`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    transition: background-color 0.3s ease, color 0.3s ease;
  }
  body {
    font-family: ${props => props.theme.typography.fontFamily};
    background-color: ${props => props.theme.colors.background};
    color: ${props => props.theme.colors.text};
    line-height: 1.6;
    scroll-behavior: smooth;
    overflow-x: hidden;
  }
  a {
    text-decoration: none;
    color: ${props => props.theme.colors.primary};
    transition: color 0.3s ease;
  }
  a:hover {
    color: ${props => props.theme.colors.secondary};
  }
`;

const AnimatedShape = styled(motion.div)`
  position: absolute;
  border-radius: 50%;
  background: radial-gradient(
    circle, 
    rgba(78, 205, 196, 0.2), 
    rgba(78, 205, 196, 0)
  );
`;

const AppContainer = styled.div`
  font-family: ${props => props.theme.typography.fontFamily};
  background-color: transparent;
  color: ${props => props.theme.colors.text};
  min-height: 100vh;
  position: relative;
  z-index: 1;
`;

function App() {
  const [theme, setTheme] = useState(lightTheme);

  useEffect(() => {
    AOS.init({
      duration: 800,
      once: true,
    });
  }, []);

  const toggleTheme = () => {
    setTheme(prevTheme => 
      prevTheme.mode === 'light' ? darkTheme : lightTheme
    );
  };

  const generateShapes = () => {
    const shapes = [];
    for (let i = 0; i < 5; i++) {
      shapes.push(
        <AnimatedShape
          key={i}
          initial={{ 
            x: Math.random() * window.innerWidth, 
            y: Math.random() * window.innerHeight,
            scale: 0.5,
            opacity: 0.5
          }}
          animate={{ 
            x: [
              Math.random() * window.innerWidth, 
              Math.random() * window.innerWidth, 
              Math.random() * window.innerWidth
            ],
            y: [
              Math.random() * window.innerHeight, 
              Math.random() * window.innerHeight, 
              Math.random() * window.innerHeight
            ],
            scale: [0.5, 1, 0.5],
            opacity: [0.5, 0.8, 0.5]
          }}
          transition={{
            duration: 20,
            repeat: Infinity,
            repeatType: 'reverse',
            ease: 'easeInOut'
          }}
          style={{
            width: `${100 + Math.random() * 300}px`,
            height: `${100 + Math.random() * 300}px`,
          }}
        />
      );
    }
    return shapes;
  };

  return (
    <ThemeProvider theme={theme}>
      <Global styles={globalStyles} />
      <ThemeToggleButton onClick={toggleTheme}>
        {theme.mode === 'light' ? <FaMoon /> : <FaSun />}
      </ThemeToggleButton>
      <BackgroundAnimation />
      <Router>
        <AppContainer>
          <Navbar theme={theme} />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/projects" element={<Projects />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </AppContainer>
      </Router>
    </ThemeProvider>
  );
}

export default App;
